import React, { useState, useEffect, useContext } from "react";
import { useParams } from "react-router-dom";
import { searchPokemon } from "./Datafetch";

function PokemonDetail() {
  const [pokemons, setPokemons] = useState({});
  const [loading, setLoading] = useState(true);
  const { id } = useParams();

  const search = async (id) => {
    try {
      setLoading(false);
      const data = await searchPokemon(id);

      console.log("PokemonDetail fetched data");
      console.log(data);
      //setPokemons(data);
      setLoading(true);
    } catch (err) {}
  };

  useEffect(() => {
    search(id);
  }, [pokemons]);

  return (
    <div>
      
      
      <h2>{PokemonDetail.name}</h2>
      <img src={PokemonDetail.sprites.front_default} alt={PokemonDetail.name} />
      <p>HP: {PokemonDetail.hp}</p>
      <p>Attack: {PokemonDetail.attack}</p>
      <p>Defence: {PokemonDetail.defence}</p>
      <p>Speed: {PokemonDetail.speed}</p>
      <p>Height: {PokemonDetail.height} cm</p>
      <p>Weight: {PokemonDetail.weight} kg</p>
    
    
     </div>
  );
}

export default PokemonDetail;
