// about page components
import React from "react";

function About() {
    return (
        <div className="about">
            <h1>About</h1>
            <p>This is the Pokédex assignment for WEB2.</p>
            <p>Pokémon are creatures of all shapes and sizes who live in the 
                wild or alongside their Trainers. During their adventures, 
                Pokémon grow and become more experienced and even, 
                on occasion, evolve into stronger Pokémon.</p>
        </div>
    );
}

export default About;
