import React, { useState, useEffect, useContext } from "react";
import { useParams } from "react-router-dom";
import { searchPokemon } from "./Datafetch";
import styled from "styled-components";
import { NavLink } from "react-router-dom";
import ProgressBar from "./ProgressBar";
import "./Pokemon.css";
import ArrowBackIosIcon from "@material-ui/icons/ArrowBackIos";


function PokemonDetail() {
  const [pokemons, setPokemons] = useState({});
  const [loading, setLoading] = useState(true);
  const { id } = useParams();

  const search = async (id) => {
    try {
      setLoading(false);
      const data = await searchPokemon(id);

      console.log("PokemonDetail fetched data");
      console.log(data);
      //setPokemons(data);
      setLoading(true);
    } catch (err) {}
  };

  useEffect(() => {
    search(id);
  }, [pokemons]);

  return (
    <div>
      <Container className={pokemons.types && pokemons.types[0].type.name}>
        <Top>
          <First>
            <NavLink exact to="/home">
              <ArrowBackIosIcon fontSize="large" />
            </NavLink>
          </First>
          <Second>
            <Name>
              <H1>{pokemons.name}</H1>
              <Type>
                {pokemons.types &&
                  pokemons.types.map((type, idx) => {
                    return (
                      <Text
                        key={idx}
                        className={type.type.name}
                        id={type.type.name}
                      >
                        {type.type.name}
                      </Text>
                    );
                  })}
              </Type>
            </Name>
            <Id>#{pokemons.id}</Id>
          </Second>

          <Img src={`../images/pokedex/${pokemons.id}.png`} />
        </Top>
        <Bottom>
          <B1>
            <H2>Status</H2>
          </B1>

          {pokemons.stats &&
            pokemons.stats.map((stat, idx) => {
              return (
                <ProgressBar
                  key={idx}
                  title={stat.stat.name}
                  width={stat.base_stat}
                  text={stat.base_stat}
                />
              );
            })}
        </Bottom>
      </Container>
    </div>
  );
}

const B2 = styled.div`
  display: flex;
`;

const B1 = styled.div`
  display: flex;
  justify-content: center;
  align-items: center;
  margin-bottom: 5px;
  color: black;
`;
const H3 = styled.h3`
  color: black;
  font-size: 13px;
  letter-spacing: 1.2px;
  line-height: 1.08;
  padding: 2px;
  position: relative;
  opacity: 0.7;
  margin: 3px;
`;
const Bottom = styled.div`
  background-color: white;
  border: 2px solid;
  border-top-left-radius: 25px;
  border-top-right-radius: 25px;
  display: block;
  height: auto;
`;
const Top = styled.div`
  padding-left: 12px;
  padding-right: 12px;
`;

const Img = styled.img`
  width: 37%;
  position: relative;
  display: row;
  left: 33%;
  @media (max-width: 868px) {
    width: 100%;
    left: 3%;
  }
  @media (min-width: 899px) and (max-width: 2000px) {
    width: 37%;
    position: relative;
    display: row;
    left: 23%;
  }
`;

const Id = styled.h1`
  margin-bottom: 0;
  margin-top: 0;
  font-size: 60px;
 margin-right: 30px;
  @media (max-width: 868px) {
    margin-bottom: 0;
  margin-top: 0;
  margin-right: 3px;
  font-size: 20px;
  }
  @media (min-width: 899px) and (max-width: 2000px) {
    margin-bottom: 0;
  margin-top: 0;
  margin-right: 3px;
  font-size: 40px;
  }
`;
const H1 = styled.h1`
  margin-bottom: 0;
  margin-top: 0;
  font-size: 120px;
  color: white;
  margin-left: 30px;
  @media (max-width: 868px) {
    margin-bottom: 0;
  margin-top: 0;
  font-size: 40px;
  margin-left: 3px;
  }
  @media (min-width: 899px) and (max-width: 2000px) {
    margin-bottom: 0;
  margin-top: 0;
  font-size: 40px;
  margin-left: 3px;
  }
`;
const Text = styled.div`
@media (max-width: 868px) {
    width: 70px;
  font-size: 20px;
  }
  @media (min-width: 899px) and (max-width: 2000px) {
    width: 70px;
  font-size: 20px;
  }
  margin-right: 10px;
  font: bold;
  font-size: 60px;
  text-transform: capitalize;
  border-radius: 20px;
  box-shadow: rgb(0 0 0 / 69%) 0px 26px 30px -10px,
    rgb(0 0 0 / 73%) 0px 16px 10px -10px;
  cursor: pointer;
  margin-bottom: 10px;
  padding: 5px;
  //padding-left: 10px;
  transition: all 250ms cubic-bezier(0.25, 0.46, 0.45, 0.94) 0s;
  border: 3px solid rgba(249, 249, 249, 0.1);
  width: 300px;
  justify-content: center;
  align-items: center;
  display: flex;
`;
const H2 = styled.h2`
  @media (max-width: 868px) {
    font-size: 30px;
  }
  font-size: 40px;
`;

const Name = styled.div`
  display: block;
`;

const First = styled.div`
  display: flex;
  justify-content: space-between;
  margin-top: 20px;
`;
const Second = styled.div`
  display: flex;
  justify-content: space-between;
  margin-top: 10px;
  align-items: center;
  text-transform: capitalize;
`;
const Third = styled.div`
  display: flex;
  justify-content: space-between;
  margin-top: 10px;
  align-items: center;
  text-transform: capitalize;
  height: auto;
`;
const Type = styled.div`
  display: flex;
`;

const Container = styled.div`
  position: relative;
  min-height: calc(100vh-250px);
  overflow-x: hidden;
  display: block;
  top: 72px;
`;
const Favorite = styled.div`
padding: 10px;
`;

const Button = styled.button`
  background-color: transparent;
padding: 10px;
  
`;

const Div = styled.div`
  top: 785px;
  color: white;
  margin-top: 100px;
`;

export default PokemonDetail;
