import React, { useState, useEffect, useContext } from "react";
import { useParams } from "react-router-dom";
import {searchPokemon} from "./Datafetch";
import Header from "./Header";


function PokemonDetail() {
  const [pokemons, setPokemons] = useState({});
  const [loading, setLoading] = useState(true);
  const { id } = useParams();

  const search = async (id) => {
    try {
      setLoading(false);
      const data = await searchPokemon(id);

      console.log("PokemonDetail fetched data")
      console.log(data);
      //setPokemons(data);
      setLoading(true);
    } catch (err) {}
  };

  useEffect(() => {
    search(id);
  }, [pokemons]);

  return (
    <div>
      <Container className={pokemons.types && pokemons.types[0].type.name}>
        <Top>
          <First>
            <NavLink exact to="/home">
              <ArrowBackIosIcon fontSize="large" />
            </NavLink>
            <div>
              {" "}
              <Button onClick={clickHeart}>
                <Favorite>{heart}</Favorite>
              </Button>
            </div>
          </First>
          <Second>
            <Name>
              <H1>{pokemons.name}</H1>
              <Type>
                {pokemons.types &&
                  pokemons.types.map((type, idx) => {
                    return (
                      <Text
                        key={idx}
                        className={type.type.name}
                        id={type.type.name}
                      >
                        {type.type.name}
                      </Text>
                    );
                  })}
              </Type>
            </Name>
            <Id>#{pokemons.id}</Id>
          </Second>

          <Img src={`../images/pokedex/${pokemons.id}.png`} />
        </Top>
        <Bottom>
          <B1>
            <H2>Status</H2>
          </B1>

          {pokemons.stats &&
            pokemons.stats.map((stat, idx) => {
              return (
                <ProgressBar
                  key={idx}
                  title={stat.stat.name}
                  width={stat.base_stat}
                  text={stat.base_stat}
                />
              );
            })}
        </Bottom>
      </Container>
    </div>
  );
}

export default PokemonDetail;
